// src/CommentSection.js

import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import './CommentSection1.css';

const Comment = ({ comment, onReply, onEdit, onDelete }) => {
    // ... (existing Comment component code)
};

const CommentSection = () => {
    const [comments, setComments] = useState([]);
    const [text, setText] = useState('');

    const addComment = (e) => {
        e.preventDefault();
        if (text.trim()) {
            const newComment = {
                id: Date.now(),
                text,
                replies: [],
            };
            setComments([...comments, newComment]);
            setText('');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1 style={{ textAlign: 'center' }}>Threads App</h1>
            <form onSubmit={addComment} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '20px 0' }}>
                <textarea
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Write a comment..."
                    required
                    style={{ width: '80%', maxWidth: '400px', height: '50px', marginBottom: '10px' }} // Minimized width
                />
                <button type="submit" style={{ alignSelf: 'center' }}>Post Comment</button>
            </form>
            <div className="comment-section">
                {comments.map((comment) => (
                    <Comment
                        key={comment.id}
                        comment={comment}
                        onReply={addReply}
                        onEdit={editComment}
                        onDelete={deleteComment}
                    />
                ))}
            </div>
        </div>
    );
};

export default CommentSection;
